import React, { useState } from 'react'
import { View, Text, TouchableOpacity, Image } from 'react-native'
import { Dialog, Chip, Portal } from 'react-native-paper'

import { useTheme } from '../../../store'

const tagz = ['Bitcoin', 'Lightning', 'Sphinx', 'Podcast', 'Crypto', 'Tech', 'Altcoins', 'Music']

export default function TheTextInput({ name, label, required, error, setValue, value, displayOnly }) {
  const theme = useTheme()
  const [showDialog, setShowDialog] = useState(false)
  let lab = `${label.en}${required ? ' *' : ''}`
  if (error) {
    lab = `${label.en} - ${error}`
  }
  if (displayOnly) lab = label.en

  function add(name) {
    const newVal = value ? [...value] : []
    if (newVal.includes(name)) {
      setValue(newVal.filter((v) => v !== name))
    } else if (newVal.length < 5) {
      newVal.push(name)
      setValue(newVal)
    }
  }
  const showTags = value && value.length && value.length > 0 ? true : false
  return (
    <>
      <TouchableOpacity onPress={() => setShowDialog(!showDialog)}>
        <Text
          style={{
            marginTop: 10,
            marginLeft: 10,
            marginBottom: 3,
            color: theme.subtitle,
          }}
        >
          Tags:
        </Text>
        <View
          style={{
            borderColor: '#ccc',
            borderRadius: 6,
            minHeight: 40,
            width: '100%',
            borderWidth: 1,
            display: 'flex',
            flexDirection: 'row',
            flexWrap: 'wrap',
            marginBottom: 20,
          }}
        >
          {showTags &&
            value.map((t) => {
              return (
                <Chip
                  key={t}
                  avatar={<Image style={{ height: 29, width: 29 }} source={{ uri: b64[t] }} />}
                  style={{ margin: 5 }}
                >
                  {t}
                </Chip>
              )
            })}
        </View>
      </TouchableOpacity>
      <Portal>
        <Dialog visible={showDialog} style={{ bottom: 10 }} onDismiss={() => setShowDialog(false)}>
          {tagz.map((name) => {
            const selected = value && value.includes(name)
            return (
              <Chip
                key={name}
                style={{
                  margin: 5,
                  backgroundColor: selected ? theme.selected : theme.bg,
                }}
                onPress={() => add(name)}
                textStyle={{ color: theme.title }}
                avatar={<Image style={{ height: 29, width: 29 }} source={{ uri: b64[name] }} />}
              >
                {name}
              </Chip>
            )
          })}
        </Dialog>
      </Portal>
    </>
  )
}

const b64 = {
  Bitcoin:
    'data:image/png;base64,',
  Lightning:
    'data:image/png;base64,',
  Sphinx:
    'data:image/png;base64,',
  Podcast:
    'data:image/png;base64,',
  Crypto:
    'data:image/png;base64,',
  Music:
    'data:image/png;base64,',
  Tech: 'data:image/png;base64,',
  Altcoins:
    'data:image/png;base64,',
}
